mapper.select:
SELECT B.* FROM (SELECT A.*, ROWNUM AS RN FROM (SELECT * FROM FAQ
WHERE 1=1
WHERE F_CATEGORY = #{f_category}
ORDER BY F_NO DESC) A) B WHERE B.RN BETWEEN #{beginRecord} AND #{endRecord}

SELECT COUNT(*) FROM FAQ
WHERE 1=1
WHERE F_CATEGORY = #{f_category}

mapper.update:
UPDATE FAQ
SET F_TITLE = #{param1},
	F_CONTENT = #{param2}
WHERE F_NO = #{param3}
