mapper.select:
SELECT *
	FROM MEMBER
  WHERE M_ID = #{param1}
   AND M_PW = #{param2}

SELECT *
	FROM SELLER
  WHERE S_ID = #{param1}
   AND S_PW = #{param2}

SELECT M_ID
	FROM MEMBER
  WHERE M_NAME = #{param1}
   AND M_EMAIL = #{param2}

SELECT S_ID
	FROM SELLER
  WHERE S_NAME = #{param1}
   AND S_EMAIL = #{param2}

SELECT M_NO
	FROM MEMBER
  WHERE M_ID = #{param1}
   AND M_NAME = #{param2}
   AND M_EMAIL = #{param3}

SELECT S_NO
	FROM SELLER
  WHERE S_ID = #{param1}
   AND S_NAME = #{param2}
   AND S_EMAIL = #{param3}

mapper.update:
UPDATE MEMBER
	SET M_PW = #{param1}
 WHERE M_ID = #{param2}

UPDATE SELLER
	SET S_PW = #{param1}
 WHERE S_ID = #{param2}
