mapper.select:
	SELECT *
		FROM CLUBLIST CL JOIN CLUB C ON CL.C_NO = C.C_NO
			JOIN MEMBER M ON M.M_NO = C.M_NO
		WHERE CL.M_NO = #{param1}
 			AND C.C_PART = #{param2}
 			AND TO_CHAR(C.C_ENDDATE, 'YYYY-MM-DD') < TO_CHAR(SYSDATE, 'YYYY-MM-DD')

mapper.update:
UPDATE CLUB
	SET C_DELETE = 1
 WHERE M_NO = #{param1}

UPDATE PLACE
	SET P_DELETE = 1
 WHERE S_NO = #{param1}

mapper.select:
SELECT COUNT(*)
	FROM CLUBLIST 
 WHERE C_NO = #{param1}

SELECT C_NO, C_TITLE, C_DESC, C_CONTENT, C_STARTDATE, C_ENDDATE, C_MIN, C_MAX, C_MAINIMG, C_IMG1, C_IMG2, C_IMG3, C_SUBCONTENT1, C_SUBCONTENT2, C_SUBCONTENT3, C_PART
	FROM CLUB
 WHERE C_NO = #{param1}

mapper.update:
		UPDATE CLUBLIST
			SET CL_CARD += 1
 		 WHERE CL_NO = #{param1}

mapper.select:
		SELECT CL_CARD
			FROM CLUBLIST 
 		 WHERE CL_NO = #{param1}
