mapper.update:
UPDATE RESERVATION
SET RES_EMAIL=#{res_email}, RES_PRICE=#{res_price}, RES_APPLYNUM=#{res_applynum}, RES_PURPOSE=#{res_purpose}, RES_REQUIREMENT=#{res_requirement} 	RES_STATE='예약대기'
WHERE RES_NO=#{res_no}

UPDATE RESERVATION
   SET RES_STATE = '예약완료'
 WHERE RES_NO = #{res_no}

UPDATE RESERVATION
   SET RES_STATE = '예약취소'
 WHERE RES_NO = #{res_no}
