mapper.select:
SELECT S_PW
FROM SELLER
WHERE S_NO= #{s_no}

mapper.update:
UPDATE SELLER
SET S_PW=#{s_pw}
WHERE S_NO = #{s_no}

UPDATE SELLER
SET S_PHONE=#{s_phone},
	S_EMAIL=#{s_email}
WHERE S_NO = #{s_no}

mapper.select:
SELECT COUNT(*)
FROM SELLER
WHERE S_NO= #{s_no}

SELECT COUNT(*)
FROM SELLER
WHERE S_NO= #{s_no}
